mapper.select:
		SELECT 

		FROM sett_city_detail a




				AND a.sett_date BETWEEN #{beginSettDate} AND #{endSettDate}


				AND b.org_name = #{issueOrgName}


				AND c.org_name = #{billOrgName}


				AND o.name = #{settOrgCode}


				AND a.roam_type = #{roamType}



        ${sqlMap.dsf} 


				ORDER BY ${page.orderBy},a.trade_date,a.trade_time desc


				ORDER BY a.trade_date,a.trade_time desc

		SELECT     round(sum(a.trade_charge)/100,2) AS "tradeCharge",
		round(sum(a.service_charge)/100,2) AS "serviceCharge",
		round(sum(a.issue_charge)/100,2) AS "issueCharge",
		round(sum(a.bill_charge)/100,2) AS "billCharge",
		round(sum(a.center_charge)/100,2) AS "centerCharge",
		round(sum(a.sett_charge)/100,2) AS "settCharge",
		count(*) || '笔' AS "cardNo"
		FROM sett_city_detail a




				AND a.sett_date BETWEEN #{beginSettDate} AND #{endSettDate}


				AND b.org_name = #{issueOrgName}


				AND c.org_name = #{billOrgName}


				AND o.name = #{settOrgCode}


				AND a.roam_type = #{roamType}



        ${sqlMap.dsf} 


				ORDER BY ${page.orderBy}
